        public static bool CheckNumberPassword(string password)
        {
            if (password == "")
                return false;
            foreach (char c in password)
                if (!((byte)c >= 48 && (byte)c <= 57))
                    return false;
            return true;
        }
        public static bool InvalidCharacters(string Name)
        {
            if (Name.Length > 16)
                Name = Name.Substring(0, 16);
            if (Name == "")
                return false;
            if (Name == " ")
                return false;
            foreach (char c in Name)
                if (MTA.Kernel.InvalidCharacters.Contains(c) || (byte)c < 48)
                    return true;
            return true;
        }
        public static bool InvalidCharacters3(string Name)
        {
            MySqlReader reader = new MySqlReader(new MySqlCommand(MySqlCommandType.SELECT).Select("entities").Where("namechange", Name));
            if (reader.Read())
                return false;
            return true;
        }

        public static bool InvalidCharacters2(string Name)
        {
            using (var rdr = new MySqlReader(new MySqlCommand(MySqlCommandType.SELECT).Select("entities").Where("name", Name)))
                return rdr.Read();
        }
        public static bool CheckNumberPassword2(string password)
        {
            if (password == "")
                return false;
            foreach (char c in password)
                if (!((byte)c >= 90 && (byte)c <= 150))
                    return true;
            return true;
        }

        private static bool Exists(string username)
        {
            try
            {
                using (var cmd = new MySqlCommand(MySqlCommandType.SELECT).Select("accounts").Where("Username", username))
                using (var reader = new MySqlReader(cmd))
                {
                    if (reader.Read())
                    {
                        return true;
                    }
                }
            }
            catch
            {
            }
            return false;
        }
