                                                    Map dynamicMap = origMap.MakeDynamicMap();
                                                    client.Entity.Teleport(origMap.ID, dynamicMap.ID, (ushort)Kernel.Random.Next(35, 70), (ushort)Kernel.Random.Next(35, 70));
                                                    client.AI.Bot.Entity.Teleport(origMap.ID, dynamicMap.ID, (ushort)Kernel.Random.Next(35, 70), (ushort)Kernel.Random.Next(35, 70));
                                                    break;
                                                }
                                                else
                                                {
                                                    dialog.Text("Invaild Difficulty");
                                                    dialog.Option("I Try again", 255);
                                                    dialog.Send();
                                                    break;
                                                }

                                        }
                                        var PlayerList = Program.Values.Where(c => c.LobbySignup == true).ToArray();
                                        if (PlayerList != null)
                                        {
                                            var sslist = PlayerList.Where(c => c.MatchType == MaTrix.Lobby.MatchType.FBSS).ToArray();
                                            var alllist = PlayerList.Where(c => c.MatchType == MaTrix.Lobby.MatchType.AllSkills).ToArray();

                                            Kernel.SendWorldMessage(new Message("Lobby Stats", Message.FirstRightCorner), Program.Values, MaTrix.Lobby.Map.ID);
                                            Kernel.SendWorldMessage(new Message("----- Players Count : " + PlayerList.Length + " -----", Message.ContinueRightCorner), Program.Values, MaTrix.Lobby.Map.ID);
                                            if (sslist != null)
                                                Kernel.SendWorldMessage(new Message("----- Players (FB SS) Count : " + sslist.Length + " -----", Message.ContinueRightCorner), Program.Values, MaTrix.Lobby.Map.ID);
                                            if (sslist != null)
                                                Kernel.SendWorldMessage(new Message("----- Players (All skils) Count : " + alllist.Length + " -----", Message.ContinueRightCorner), Program.Values, MaTrix.Lobby.Map.ID);
                                        }
                                        break;
                                    }
                                }
                            }
